def test_eq_real():
    x = Real.fromint(10)
    y = Real.fromint(10)
    res = x.eq(y)
    assert res == True
    x = Real.fromint(10)
    y = Real.fromint(-3)
    res = x.eq(y)
    assert res == False
    x = Real.fromint(2, 7)
    y = Real.fromint(2, 7)
    res = x.eq(y)
    assert res == True
    x = Real.fromint(2, 9)
    y = Real.fromint(2, 7)
    res = x.eq(y)
    assert res == False

def test_lt_real():
    x = Real.fromint(10)
    y = Real.fromint(11)
    res1 = x.lt(y)
    res2 = y.lt(x)
    assert res1 == True
    assert res2 == False
    x = Real.fromint(-10)
    y = Real.fromint(2)
    res1 = x.lt(y)
    res2 = y.lt(x)
    assert res1 == True
    assert res2 == False
    x = Real.fromint(10)
    y = Real.fromint(10)
    res = x.lt(y)
    assert res == False
    x = Real.fromint(1, 3)
    y = Real.fromint(5, 2)
    res = x.lt(y)
    assert res == True
    x = Real.fromint(1, 3)
    y = Real.fromint(1, 3)
    res = x.lt(y)
    assert res == False
    x = Real.fromint(-1, 3)
    y = Real.fromint(1, 6)
    res = x.lt(y)
    assert res == True
    x = Real.fromint(1, -3)
    y = Real.fromint(1, -6)
    res = x.lt(y)
    assert res == True

